import {
  clipboardCopy
} from "./chunk-LXKNZLEF.js";

// src/lib/hooks/clipboard.ts
import { ref, unref } from "vue";
var useClipboard = (delay = 3e3) => {
  const copied = ref(false);
  const copy = (text) => {
    const copyText = unref(text);
    clipboardCopy(copyText).then(() => {
      copied.value = true;
      const timer = setTimeout(() => {
        copied.value = false;
        clearTimeout(timer);
      }, delay);
    });
  };
  return {
    copied,
    copy
  };
};

export {
  useClipboard
};
