// src/lib/hooks/create-global-state.ts
import { effectScope } from "vue";
function createGlobalState(stateFactory) {
  let initialized = false;
  let state;
  const scope = effectScope(true);
  return () => {
    if (!initialized) {
      state = scope.run(stateFactory);
      initialized = true;
    }
    return state;
  };
}

export {
  createGlobalState
};
