import {
  isFunction
} from "./chunk-AHWDZYGD.js";

// src/lib/safe-next-tick.ts
var delayTimer = async (delay = 0) => {
  return new Promise((resolve) => {
    const timer = setTimeout(() => {
      resolve(true);
      clearTimeout(timer);
    }, delay);
  });
};
async function safeNextick(fn, option) {
  await delayTimer(option?.delay);
  if (fn && isFunction(fn))
    fn();
}

export {
  delayTimer,
  safeNextick
};
