import * as vue from 'vue';
import { WatchOptions, ComputedRef } from 'vue';
import { MaybeComputedRef } from './shared.js';

interface UseClonedOptions<T = any> extends WatchOptions {
    /**
       * Custom clone function.
       *
       * By default, it use `JSON.parse(JSON.stringify(value))` to clone.
       */
    clone?: (source: T) => T;
    /**
       * Manually sync the ref
       *
       * @default false
       */
    manual?: boolean;
}
interface UseClonedReturn<T> {
    /**
       * Cloned ref
       */
    cloned: ComputedRef<T>;
    /**
       * Sync cloned data with source manually
       */
    sync: () => void;
}
type CloneFn<F, T = F> = (x: F) => T;
declare function cloneFnJSON<T>(source: T): T;
declare function useCloned<T>(source: MaybeComputedRef<T>, options?: UseClonedOptions): {
    cloned: vue.Ref<vue.UnwrapRef<T>>;
    sync: () => void;
};

export { CloneFn, UseClonedOptions, UseClonedReturn, cloneFnJSON, useCloned };
