import { Ref } from 'vue';

type Updater<T> = T | ((prevValue: T) => T);
type SetState<T> = (nextValue: Updater<T>, 
/**
     * Will not update state when destroyed.
     * Developer should make sure this is safe to ignore.
     */
ignoreDestroy?: boolean) => void;
declare const useState: <T = any>(defaultValue?: T | (() => T) | undefined) => [Ref<T>, SetState<T>];

export { SetState, useState };
