/**
 * Represents the options for the configuration plugin.
 * 表示配置插件的选项。
 */
interface Options {
    appName?: string;
    baseDir?: string;
    configFile?: {
        generate?: boolean;
        fileName?: string;
        outputDir?: string;
    };
    htmlInjection?: {
        enable?: boolean;
        templates?: string[];
        position?: "head" | "body" | "head-prepend" | "body-prepend";
        decodeEntities?: boolean;
    };
    envVariables?: {
        prefix?: string;
        files?: string[];
    };
}
/**
 * Represents the options for building a configuration file.
 * 表示构建配置文件的选项。
 */
interface BuildConfigOptions {
    configName: string;
    config: any;
    configFileName?: string;
    appName?: string;
    outputDir: string;
}

export type { BuildConfigOptions, Options };
